import React,{useState,useEffect} from "react";
import Button from "../../helper/button"
import "./style.css"
/*
const CountDown = () => {
  const [countdownDate, setCountdownDate] = useState(new Date('2022-03-01T06:00:00').getTime())
*/
const CountDown = ({date}) => {
  const countdownDate = new Date(date).getTime();
  const [state, setState] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0,
  });

 
 
  useEffect(() => {
    setInterval(() => { 
      if (countdownDate) {
        const currentTime = new Date().getTime();

        const distanceToDate = countdownDate - currentTime;

        let days = Math.floor(distanceToDate / (1000 * 60 * 60 * 24));
        let hours = Math.floor(
          (distanceToDate % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60),
        );
        let minutes = Math.floor(
          (distanceToDate % (1000 * 60 * 60)) / (1000 * 60),
        );
        let seconds = Math.floor((distanceToDate % (1000 * 60)) / 1000);

        const numbersToAddZeroTo = [1, 2, 3, 4, 5, 6, 7, 8, 9];

        days = `${days}`;
        if (numbersToAddZeroTo.includes(hours)) {
          hours = `0${hours}`;
        } else if (numbersToAddZeroTo.includes(minutes)) {
          minutes = `0${minutes}`;
        } else if (numbersToAddZeroTo.includes(seconds)) {
          seconds = `0${seconds}`;
        }

        setState({ days: days, hours: hours, minutes, seconds });
      }
    }, 1000);
  }, []);

  let dateIn = new Date(date).getTime() - new Date().getTime();

  return (
    <>
    {/* { 0 < state.days && 0 < state.hours && 0 < state.minutes && 0 < state.seconds ?   */}
    <>
      {dateIn >= 0 ? 
      
      <div className="countDownItIs">
        <h4 className="title">PRESALE START IN</h4>
        <div className='countdown-wrapper'>
          <div className='time-section'>
            <div className='time'>{state.days || '0'}</div>
            <small className="time-text">Days</small>
          </div>
          <div className='time-section'>
            <div className='time'>{state.hours || '00'}</div>
            <small className="time-text">Hours</small>
          </div>
          <div className='time-section'>
            <div className='time'>{state.minutes || '00'}</div>
            <small className="time-text">Minutes</small>
          </div>
          <div className='time-section'>
            <div className='time'>{state.seconds || '00'}</div>
            <small className="time-text">Seconds</small>
          </div>
        </div>
        <Button classes={"theme-btn"} text="Buy Presale" />
      </div>
      : ""}
    </>
    {/* : ""}  */}
    </>
  );
};

export default CountDown;
